import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { Carousel } from 'react-bootstrap';
import MyContext from '../context/MyContext';

function RecomendationCards({ type }) {
  let recomendation = [];
  const { allFood, allDrink } = useContext(MyContext);
  const MAX_LENGTH = 6;

  if (allFood.length > MAX_LENGTH || allDrink.length > MAX_LENGTH) {
    allFood.length = 6;
    allDrink.length = 6;
  }

  if (type === 'drink') {
    recomendation = allDrink;
  } else {
    recomendation = allFood;
  }

  return (
    <>
      {recomendation.map(({ strCategory }, index) => (
        <div
          data-testid={ `${index}-recomendation-card` }
          key={ index }
        >
          {strCategory}

        </div>

      ))}
      <Carousel>
        <Carousel.Item data-testid={ `${index}-recomendation-card` }>
          <img
            className="d-block w-100"
            src="https://picsum.photos/500/300?img=1"
            alt="First slide"
          />
          <Carousel.Caption>
            <h3>First slide label</h3>
            <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://picsum.photos/500/300?img=2"
            alt="Second slide"
          />
          <Carousel.Caption>
            <h3>Second slide label</h3>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://picsum.photos/500/300?img=3"
            alt="Third slide"
          />
          <Carousel.Caption>
            <h3>Third slide label</h3>
            <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>

    </>
  );
}

export default RecomendationCards;

RecomendationCards.propTypes = {
  type: PropTypes.string,
}.isRequired;
