import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import ShareIcon from '../images/shareIcon.svg';
import BlackHeartIcon from '../images/blackHeartIcon.svg';

function FavoriteCard({ recipe }) {
  return (
    <div>
      {recipe.map((element, index) => {
        if (element.type === 'food') {
          const removeFromFavorite = () => {
            const recipeList = JSON.parse(localStorage.getItem('favoriteRecipes'));
            const newList = recipeList.filter((item) => item.id !== element.id);
            localStorage.setItem('favoriteRecipes', JSON.stringify(newList));
            window.location.reload();
          };
          const copyToClipboard = async () => {
            await navigator.clipboard.writeText(`http://localhost:3000/foods/${element.id}`);
          };
          return (
            <div key={ index }>
              <Link to={ `foods/${element.id}` }>
                <img
                  data-testid={ `${index}-horizontal-image` }
                  width={ 100 }
                  src={ element.image }
                  alt={ element.name }
                />
              </Link>
              <p>{`${element.nationality} - ${element.category}`}</p>
              <h3>{element.name}</h3>
              <input
                onClick={ copyToClipboard }
                type="image"
                src={ ShareIcon }
                alt="ícone de compartilhamento"
              />
              <input
                onClick={ removeFromFavorite }
                type="image"
                src={ BlackHeartIcon }
                alt="iconde de coração preenchido"
              />
            </div>);
        }
        const removeFromFavorite = () => {
          const recipeList = JSON.parse(localStorage.getItem('favoriteRecipes'));
          const newListDrink = recipeList.filter((item) => item.id !== element.id);
          localStorage.setItem('favoriteRecipes', JSON.stringify(newListDrink));
          window.location.reload();
        };
        const copyToClipboard = async () => {
          await navigator.clipboard.writeText(`http://localhost:3000/drinks/${element.id}`);
        };
        return (
          <div key={ index }>
            <Link to={ `drinks/${element.id}` }>
              <img
                width={ 100 }
                src={ element.image }
                alt={ element.name }
              />
            </Link>
            <p>{`${element.alcoholicOrNot}`}</p>
            <h3>{element.name}</h3>
            <input
              onClick={ copyToClipboard }
              type="image"
              src={ ShareIcon }
              alt="ícone de compartilhamento"
            />
            <input
              onClick={ removeFromFavorite }
              type="image"
              src={ BlackHeartIcon }
              alt="iconde de coração preenchido"
            />
          </div>
        );
      })}
    </div>
  );
}

export default FavoriteCard;
FavoriteCard.propTypes = {
  needRender: PropTypes.bool,
}.isRequired;
