import React, { useState } from 'react';
import { fetchApiIngredient,
  fetchApiName, fetchApiFirstLetter } from '../services/fetchApi';

function SearchBar() {
  const [searchRadio, setSearchRadio] = useState('');
  const [searchValue, setSearchValue] = useState('');

  const searchClick = async () => {
    console.log('cliquei no search meu valor é', searchRadio);
    if (searchRadio === 'ingredient') {
      await fetchApiIngredient('ingrediente');
    }
    if (searchRadio === 'radioName') {
      await fetchApiName('nome');
    }
    if (searchRadio === 'firstLetter') {
      await fetchApiFirstLetter('primeira-letra');
    }
  };

  return (
    <div>
      <div>
        <input
          data-testid="search-input"
          type="text"
          placeholder="Buscar"
        />
      </div>
      <div>
        <label htmlFor="ingredient-radio">
          <input
            data-testid="ingredient-search-radio"
            type="radio"
            name="search-radio"
            id="ingredient-radio"
            onChange={ () => setSearchRadio('ingredient') }
          />
          Ingredient
        </label>
        <label htmlFor="name-radio">
          <input
            data-testid="name-search-radio"
            type="radio"
            name="search-radio"
            id="name-radio"
            onChange={ () => setSearchRadio('radioName') }
          />
          Name
        </label>
        <label htmlFor="first-letter-radio">
          <input
            data-testid="first-letter-search-radio"
            type="radio"
            name="search-radio"
            id="first-letter-radio"
            onChange={ () => setSearchRadio('firstLetter') }
          />
          First Letter
        </label>
        <div>
          <button
            data-testid="exec-search-btn"
            type="button"
            onClick={ searchClick }
          >
            Search

          </button>
        </div>
      </div>
    </div>
  );
}

export default SearchBar;
