import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { Carousel } from 'react-bootstrap';
import MyContext from '../context/MyContext';

function RecomendationCards({ type }) {
  let recomendation = [];
  const { allFood, allDrink } = useContext(MyContext);
  const MAX_LENGTH = 6;

  if (allFood.length > MAX_LENGTH || allDrink.length > MAX_LENGTH) {
    allFood.length = 6;
    allDrink.length = 6;
  }

  if (type === 'drink') {
    recomendation = allDrink;
  } else {
    recomendation = allFood;
  }

  return (
    <>
      {recomendation.map(({ strCategory }, index) => (
        <div
          data-testid={ `${index}-recomendation-card` }
          key={ index }
        >
          {strCategory}

        </div>

      ))}
      <Carousel>

        <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
          <ol className="carousel-indicators">
            <li
              data-target="#carouselExampleIndicators"
              data-slide-to="0"
              className="active"
            />
            <li data-target="#carouselExampleIndicators" data-slide-to="1" />
            <li data-target="#carouselExampleIndicators" data-slide-to="2" />
          </ol>
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img
                className="d-block w-100"
                src="..."
                alt="First slide"
              />
            </div>
            <div className="carousel-item">
              <img className="d-block w-100" src="..." alt="Second slide" />
            </div>
            <div className="carousel-item">
              <img className="d-block w-100" src="..." alt="Third slide" />
            </div>
          </div>
          <a
            className="carousel-control-prev"
            href="#carouselExampleIndicators"
            role="button"
            data-slide="prev"
          >
            <span className="carousel-control-prev-icon" aria-hidden="true" />
            <span className="sr-only">Previous</span>
          </a>
          <a
            className="carousel-control-next"
            href="#carouselExampleIndicators"
            role="button"
            data-slide="next"
          >
            <span className="carousel-control-next-icon" aria-hidden="true" />
            <span className="sr-only">Next</span>
          </a>
        </div>
      </Carousel>
    </>
  );
}

export default RecomendationCards;

RecomendationCards.propTypes = {
  type: PropTypes.string,
}.isRequired;
