async function fetchApi(name) {
  try {
    const url = `https://www.themealdb.com/api/json/v1/1/filter.php?i=${name}`;
    const response = await fetch(url);
    const data = response.json();
    return data;
  } catch (error) { console.log(error); }
}

export default fetchApi;
