import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import ShareIcon from '../images/shareIcon.svg';
import WhiteHeartIcon from '../images/whiteHeartIcon.svg';
import BlackHeartIcon from '../images/blackHeartIcon.svg';
import FavoriteRecipes from '../pages/FavoriteRecipes';

function ShareAndFav({ actualFood: food, typeOfCard }) {
  const [linkCopied, setLinkCopied] = useState(false);

  useEffect(() => {
    const isFavoriteRecipe = (recipe) => {
      const recipeList = JSON.parse(localStorage.getItem('favoriteRecipes'));
      if (recipeList) return recipeList.find((item) => item.id === recipe.id);
      return false;
    };
    isFavoriteRecipe(food);
  });

  const copyToClipboard = async () => {
    await navigator.clipboard.writeText(`${window.location}`);
    setLinkCopied(true);
  };

  const isFavoriteRecipe = (recipe) => {
    const recipeList = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (recipeList) return recipeList.find((item) => item.id === recipe.id);
    return false;
  };

  let actualState = {};
  if (typeOfCard === 'food') {
    const { idMeal: id,
      strCategory: type,
      strArea: nationality,
      strCategory: category,
      strMeal: name, strMealThumb: image } = food[0];
    actualState = { id, type, nationality, category, name, image };
  }
  const { idDrink: id,
    strCategory: type,
    strArea: nationality,
    strCategory: category,
    strDrink: name, strDrinkThumb: image } = food[0];
  actualState = { id, type, nationality, category, name, image };

  const addToFavoriteRecipeState = () => {
    const recipeList = JSON.parse(localStorage.getItem('favoriteRecipes'));
    let newList = [];
    if (recipeList) {
      newList = recipeList.find((item) => item.id === actualState.id)
        ? recipeList.filter((item) => item.id !== actualState.id)
        : [...recipeList, actualState];
    } else {
      newList = [actualState];
    }
    localStorage.setItem('favoriteRecipes', JSON.stringify(newList));
  };

  return (
    <div>
      <p data-testid="recipe-category">{food[0].strCategory}</p>
      {linkCopied ? 'Link copied!'
        : (
          <input
            onClick={ copyToClipboard }
            data-testid="share-btn"
            type="image"
            src={ ShareIcon }
            alt="ícone de compartilhamento"
          />)}
      <input
        onClick={ addToFavoriteRecipeState }
        data-testid="favorite-btn"
        type="image"
        src={ isFavoriteRecipe(food) ? BlackHeartIcon : WhiteHeartIcon }
        alt="ícone de coração para favoritar"
      />
    </div>
  );
}

export default ShareAndFav;

ShareAndFav.propTypes = {
  food: PropTypes.string,
}.isRequired;
