import React from 'react';
import PropTypes from 'prop-types';

function RecomendationCards({ type }) {
  const [recomendation, setRecomendation] = useState([]);
  if (type === 'drink') {
    const fetchRecomendation = async () => {
      const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s=';
      const data = await fetch(url);
      const response = data.json();
      setRecomendation(response.drinks);
    };
    fetchRecomendation();
  } else {
    const fetchRecomendation = async () => {
      const url = 'https://www.themealdb.com/api/json/v1/1/search.php?s=';
      const data = await fetch(url);
      const response = data.json();
      setRecomendation(response.meals);
    };
    fetchRecomendation();
  }
  return (
    <div>
      {recomendation.map((element, index) => (
        <div key={ index }>
          <p>{element.strCategory}</p>
        </div>
      ))}
    </div>

  );
}

export default RecomendationCards;

RecomendationCards.propTypes = {
  type: PropTypes.string,
}.isRequired;
