import React from 'react';
import PropTypes from 'prop-types';

function FavoriteCard({ recipe }) {
  console.log(recipe);
  return (
    <div>
      {recipe.map((element, index) => {
        if (element.type === 'food') {
          const fetchId = async () => {
            const url = `https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idNumber}`;
            const response = await fetch(url);
            const data = await response.json();
            const recipeId = data.meals;
            return recipeId;
          };
          fetchId;
          return (
            <div key={ index }>
              <img
                width={ 100 }
                src={ element.image }
                alt={ element.name }
              />
              <p>{`${element.nationality} - ${element.category}`}</p>
              <h3>{element.name}</h3>
            </div>);
        }
        return (
          <div key={ index }>
            <img
              width={ 100 }
              src={ element.image }
              alt={ element.name }
            />
            <p>{`${element.alcoholicOrNot}`}</p>
            <h3>{element.name}</h3>
          </div>
        );
      })}
    </div>
  );
}

export default FavoriteCard;
FavoriteCard.propTypes = {
  needRender: PropTypes.bool,
}.isRequired;
